INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (1, '01.001', 'Trabalho ou operações, em contato permanente com pacientes em isolamento por doenças infecto-contagiosas, bem como objetos de seu uso, não previamente esterilizados.', now(), 1, False);
