INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (1, '01.005', 'Trabalhos e operações em contato permanente com pacientes, animais ou com material infecto-contagiante, em hospitais, serviços de emergência, enfermarias, ambulatórios, postos de vacinação e outros estabelecimentos destinados aos cuidados da saúde humana.', now(), 1, False);
