INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (1, '01.007', 'Trabalhos e operações em contato permanente com pacientes, animais ou com material infecto-contagiante, em contato em laboratórios, com animais destinados ao preparo de soro, vacinas e outros produtos. Inclui os trabalhos com animais infectados para tratamento ou para o preparo de soro, vacinas e outros produtos.', now(), 1, False);
