INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (4, '04.031', 'Carga e descarga de vasilhames contendo inflamáveis líquidos ou vasilhames vazios não desgaseificados ou decantados, transportados pôr navios, chatas ou batelões (Afastamento de 3 metros da beira do cais, durante a operação, com extensão correspondente ao comprimento da embarcação).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.001', 'Produção, utilização, processamento, transporte, guarda, estocagem, e manuseio de materiais radioativos, selados e não selados, de estado físico e forma química quaisquer, naturais ou artificiais (Minas e depósitos de materiais radioativos. Plantas-piloto e usinas de beneficiamento de minerais radioativos. Outras áreas sujeitas a risco potencial devido às radiações ionizantes), incluindo: a) Prospecção, mineração, operação, beneficiamento e processamento de minerais radioativos (Lixiviação de minerais radioativos para a produção de concentrados de urânio e tório. Purificação de concentrados e conversão em outras formas para uso como combustível nuclear); b) Produção, transformação e tratamento de materiais nucleares para o ciclo do combustível nuclear (Produção de fluoretos de urânio para a produção de hexafluoreto e urânio metálico. Instalações para enriquecimento isotópico e reconversão. Fabricação do elemento combustível nuclear. Instalações para armazenamento dos elementos combustíveis usados. Instalações para o retratamento do combustível irradiado Instalações para o tratamento e deposições, provisórias e finais, dos rejeitos radioativos naturais e artificiais); c) Produção de radioisótopos para uso em medicina, agricultura agropecuária, pesquisa científica e tecnológica (Laboratórios para a produção de radioisótopos e moléculas marcadas); d) Produção de Fontes Radioativas (Instalações para tratamento do material radioativo e confecção de fontes. Laboratórios de testes, ensaios e calibração de fontes, detectores e monitores de radiação, com fontes radioativas) e) Testes, ensaios e calibração de detectores e monitores de radiação com fontes de radiação (Laboratórios de ensaios para materiais radioativos. Laboratórios de radioquímica); f) Descontaminação de superfícies, instrumentos, máquinas, ferramentas, utensílios de laboratório, vestimentas e de quaisquer outras áreas ou bens duráveis contaminados com material radioativo (Laboratórios para descontaminação de peças e materiais radioativos Coleta de rejeitos radioativos em instalações, prédios e em áreas abertas. Lavanderia para roupas contaminadas. Transporte de materiais e rejeitos radioativos, condicionamento, estocagens e sua deposição); g) Separação isotópica e processamento radioquímico (Instalações para tratamento, condicionamento, contenção, estabilização, estocagem e deposição de rejeitos radioativos. Instalações para retenção de rejeitos radioativos);', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.002', 'h) Manuseio, condicionamento, liberação, monitoração, estabilização, inspeção, retenção e deposição de rejeitos radioativos (Sítio de rejeitos. Instalações para estocagem de produtos radioativos para posterior aproveitamento). Atividades de operação e manutenção de reatores nucleares (Edifícios de reatores; Edifícios de estocagem de combustível), incluindo: a) montagem, instalação, substituição e inspeção de elementos combustíveis (Instalações de tratamento e estocagem de rejeitos radioativos); b) Manutenção de componentes integrantes do reator nuclear e dos sistemas hidráulicos mecânicos e elétricos, irradiados, contaminados ou situados em áreas de radiação (Instalações para tratamento de água de reatores e separação e contenção de produtos radioativos; Salas de operação de reatores; Salas de amostragem de efluentes radioativos); c) Manuseio de amostras irradiadas na operação e manutenção de reatores nucleares (Laboratórios de medidas de radiação); d) Experimentos utilizando canais de irradiação na operação e manutenção de reatores nucleares (Outras áreas sujeitas a risco potencial às radiações ionizantes passíveis de serem atingidas por dispersão de produtos voláteis); e) Medição de radiação, levantamento de dados radiológicos e nucleares, ensaios, testes, inspeções, fiscalização e supervisão de trabalhos técnicos na operação e manutenção de reatores nucleares (Laboratórios semiquentes e quentes; Minas de urânio e tório; Depósitos de minerais radioativos e produtos do tratamento de minerais radioativos); f) Segregação, manuseio, tratamento, acondicionamento e armazenamento de rejeitos radioativos na operação e manutenção de reatores nucleares (Segregação, manuseio, tratamento, acondicionamento e armazenamento de rejeitos radioativos).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.003', 'Atividades de operação e manutenção de aceleradores de partículas (áreas de irradiação de alvos), incluindo: a) Montagem, instalação, substituição e manutenção de componentes irradiados ou contaminados (Oficinas de manutenção de componentes irradiados ou contaminados; Salas de operação de aceleradores); b) Processamento de alvos irradiados (laboratórios para tratamento de alvos irradiados e separação de radioisótopos); c) Experimentos com feixes de partículas (laboratórios de testes com radiação e medidas nucleares); d) Medição de radiação, levantamento de dados radiológicos e nucleares, testes, inspeções e supervisão de trabalhos técnicos (Áreas de tratamento e estocagem de rejeitos radioativos); e) Segregação, manuseio, tratamento, acondicionamento e armazenamento de rejeitos radioativos (Laboratórios de processamento de alvos irradiados).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.004', 'Atividades de operação com aparelhos de raios-X, com irradiadores de radiação gama, radiação beta ou radiação de nêutrons (Salas de irradiação e de operação de aparelhos de raios-X e de irradiadores gama, beta ou nêutrons), incluindo: a) diagnóstico médico e odontológico (Laboratórios de testes, ensaios e calibração com as fontes de radiação descritas); b) Laboratórios de processamento de alvos irradiados; c) Radiografia industrial, gamagrafia e neutronradiografia (Manuseio de fontes); d) Análise de materiais por difratometria (Manuseio do equipamento); e) Testes, ensaios e calibração de detectores e monitores de radiação (Manuseio de fontes e amostras radioativas); f) Irradiação de alimentos (Manuseio de fontes e instalações para a irradiação de alimentos); g) Esterilização de instrumentos médico-hospitalares (Manuseio de fontes e instalações para a operação); h) Irradiação de espécimes minerais e biológicos (Manuseio de amostras irradiadas); i) Medição de radiação, levantamento de dados radiológicos ensaios, testes, inspeções, fiscalização de trabalhos técnicos (Laboratórios de ensaios e calibração de fontes e materiais radioativos).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.005', 'Atividades de medicina nuclear (Salas de diagnóstico e terapia com medicina nuclear).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.006', 'Manuseio e aplicação de radioisótopos para diagnóstico médico e terapia (Enfermaria de pacientes, sob treinamento com radioisótopos. Enfermaria de pacientes contaminados com radioisótopos em observação e sob tratamento de descontaminação).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.007', 'Atividades de medicina nuclear: Manuseio de fontes seladas para aplicação em braquiterapia (Área de tratamento e estocagem de rejeitos radioativos).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.008', 'Atividades de medicina nuclear: Obtenção de dados biológicos de pacientes com radioisótopos incorporados (Manuseio de materiais biológicos contendo radioisótopos ou moléculas marcadas).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.009', 'Atividades de medicina nuclear: Segregação, manuseio, tratamento, acondicionamento e estocagem de rejeitos radioativos em medicina nuclear (Laboratórios para descontaminação e coleta de rejeitos radioativos).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.010', 'Descomissionamento de instalações nucleares e radioativas (Áreas de instalações nucleares e radioativas contaminadas e com rejeitos) que inclui: a) Todas descontaminações radioativas inerentes (Depósitos provisórios e definitivos de rejeitos radioativos); b) Gerenciamento dos rejeitos radioativos existentes, ou sejam: tratamento e acondicionamento dos rejeitos líquidos, sólidos, gasosos e aerossóis; transporte e deposição dos mesmos (Instalações para contenção de rejeitos radioativos. Instalações para asfaltamento de rejeitos radioativos. Instalações para cimentação de rejeitos radioativos).', now(), 1, False);
INSERT INTO public.esocial_atividades_periculosas_insalubres_especiais (grupo, codigo, descricao, criado_em, criado_por_id, excluido) VALUES (5, '05.011', 'Descomissionamento de minas, moinhos e usinas de tratamento de minerais radioativos (Tratamento de rejeitos minerais. Repositório de rejeitos naturais (bacia de contenção de rádio e outros radioisótopos). Deposição de gangas e rejeitos de mineração).', now(), 1, False);
