INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('1.0.01', 'Lesão corporal que cause a morte ou a perda ou redução, permanente ou temporária, da capacidade para o trabalho, desde que não enquadrada em nenhum dos demais códigos.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('1.0.02', 'Perturbação funcional que cause a morte ou a perda ou redução, permanente ou temporária, da capacidade para o trabalho, desde que não enquadrada em nenhum dos demais códigos.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('2.0.01', 'Doença profissional, assim entendida a produzida ou desencadeada pelo exercício do trabalho peculiar a determinada atividade e constante da respectiva relação elaborada pelo Ministério do Trabalho e Previdência Social, desde que não enquadrada em nenhum dos demais códigos.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('2.0.02', 'Doença do trabalho, assim entendida a adquirida ou desencadeada em função de condições especiais em que o trabalho é realizado e com ele se relacione diretamente, constante da respectiva relação elaborada pelo Ministério do Trabalho e Previdência Social, desde que não enquadrada em nenhum dos demais códigos.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('2.0.03', 'Doença proveniente de contaminação acidental do empregado no exercício de sua atividade.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('2.0.04', 'Doença endêmica adquirida por segurado habitante de região em que ela se desenvolva quando resultante de exposição ou contato direto determinado pela natureza do trabalho.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('2.0.05', 'Doença profissional ou do trabalho não incluída na relação elaborada pelo Ministério do Trabalho e Previdência Social quando resultante das condições especiais em que o trabalho é executado e com ele se relaciona diretamente.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('2.0.06', 'Doença profissional ou do trabalho enquadrada na relação elaborada pelo Ministério do Trabalho e Previdência Social relativa nexo técnico epidemiológico previdenciário - NTEP.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.01', 'Acidente ligado ao trabalho que, embora não tenha sido a causa única, haja contribuído diretamente para a morte do segurado, para redução ou perda da sua capacidade para o trabalho, ou produzido lesão que exija atenção médica para a sua recuperação.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.02', 'Acidente sofrido pelo segurado no local e no horário do trabalho, em consequência de ato de agressão, sabotagem ou terrorismo praticado por terceiro ou companheiro de trabalho.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.03', 'Acidente sofrido pelo segurado no local e no horário do trabalho, em consequência de ofensa física intencional, inclusive de terceiro, por motivo de disputa relacionada ao trabalho.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.04', 'Acidente sofrido pelo segurado no local e no horário do trabalho, em consequência de ato de imprudência, de negligência ou de imperícia de terceiro ou de companheiro de trabalho.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.05', 'Acidente sofrido pelo segurado no local e no horário do trabalho, em consequência de ato de pessoa privada do uso da razão.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.06', 'Acidente sofrido pelo segurado no local e no horário do trabalho, em consequência de desabamento, inundação, incêndio e outros casos fortuitos ou decorrentes de força maior.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.07', 'Acidente sofrido pelo segurado ainda que fora do local e horário de trabalho na execução de ordem ou na realização de serviço sob a autoridade da empresa.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.08', 'Acidente sofrido pelo segurado ainda que fora do local e horário de trabalho na prestação espontânea de qualquer serviço à empresa para lhe evitar prejuízo ou proporcionar proveito.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.09', 'Acidente sofrido pelo segurado ainda que fora do local e horário de trabalho em viagem a serviço da empresa, inclusive para estudo quando financiada por esta dentro de seus planos para melhor capacitação da mão-de- obra, independentemente do meio de locomoção utilizado, inclusive veículo de propriedade do segurado.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.10', 'Acidente sofrido pelo segurado ainda que fora do local e horário de trabalho no percurso da residência para o local de trabalho ou deste para aquela, qualquer que seja o meio de locomoção, inclusive veículo de propriedade do segurado.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('3.0.11', 'Acidente sofrido pelo segurado nos períodos destinados a refeição ou descanso, ou por ocasião da satisfação de outras necessidades fisiológicas, no local do trabalho ou durante este.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('4.0.01', 'Suspeita de doenças profissionais ou do trabalho produzidas pelas condições especiais de trabalho, nos termos do art. 169 da CLT.', now(), 1, False);
INSERT INTO public.esocial_codificacoes_acidente_trabalho (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('4.0.02', 'Constatação de ocorrência ou agravamento de doenças profissionais, através de exames médicos que incluam os definidos na NR 07; ou sendo verificadas alterações que revelem qualquer tipo de disfunção de órgão ou sistema biológico, através dos exames constantes dos Quadros I (apenas aqueles com interpretação SC) e II, e do item 7.4.2.3 desta NR, mesmo sem sintomatologia, caberá ao médico-coordenador ou encarregado.', now(), 1, False);