INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702040000', 'Queimadura ou escaldadura - efeito de temperatura elevada. Efeito do contato com substância quente. Inclui queimadura por eletricidade, mas não inclui choque elétrico. Não inclui queimadura por substância química, efeito de radiação, queimadura de sol, incapacidade sistêmica como intermação, queimadura por atrito, etc.', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702042000', 'Queimadura química (lesão de tecido provocada pela ação corrosiva de produto químico, suas emanações, etc.)', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702045000', 'Efeito de radiação (imediato) - queimadura de sol e toda forma de lesão de tecido, osso ou fluido orgânico, por exposição à radiação', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702048000', 'Congelamento, geladura e outros efeitos da exposição à baixa temperatura', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702050000', 'Asfixia, estrangulamento, afogamento', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702055000', 'Intermação, insolação, cãibra, exaustão e outros efeitos da temperatura ambiente elevada - não inclui queimadura de sol ou outros efeitos de radiação', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702060000', 'Choque elétrico e eletroplessão (eletrocussão)', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702065000', 'Hérnia de qualquer natureza, ruptura', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702070000', 'Amputação ou enucleação', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('702075000', 'Perda ou diminuição de sentido (audição, visão, olfato, paladar e tato, desde que não seja sequela de outra lesão)', now(), 1, False);
