INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('704030000', 'Pneumoconiose (silicose, asbestose, etc.)', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('704040000', 'Dermatose (erupção, inflamação da pele, inclusive furúnculo, etc.). Geralmente provocada pelo contato direto com substâncias ou agentes sensibilizantes ou irritantes, tais como medicamentos, óleos, agentes biológicos, plantas, madeiras ou metais. Não inclui lesão provocada pela ação corrosiva de produtos químicos, queimadura por contato com substâncias quentes, efeito de exposição à radiação, efeito de exposição a baixas temperaturas ou inflamação ou irritação causada por fricção ou impacto', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('704050000', 'Envenenamento sistêmico - condição mórbida sistêmica provocada por inalação, ingestão ou absorção cutânea de substância tóxica, que afete o metabolismo, o funcionamento do sistema nervoso, do aparelho circulatório, do aparelho digestivo, do aparelho respiratório, dos órgãos de excreção, do sistema músculo - esquelético, etc., inclui ação de produto químico, medicamento, metal ou peçonha. Não inclui efeito de radiação, pneumoconiose, efeito corrosivo de produto químico, irritação cutânea, septicemia ou caso de ferida infectada', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('704060000', 'Perda ou diminuição mediatas de sentido (audição, visão, olfato, paladar e tato, desde que não seja sequela de outra lesão)', now(), 1, False);
INSERT INTO public.esocial_naturezas_lesoes (codigo, descricao, criado_em, criado_por_id, excluido) VALUES ('704070000', 'Efeito de radiação (mediato) - queimadura do sol e toda forma de lesão de tecido, osso, ou fluido orgânico por exposição à radiação.', now(), 1, False);
